import React, {useState} from 'react'
import "./styles.css";
import mockup from '../../Images/Login.svg'
import mockup1 from '../../Images/Home.svg'
import mockup2 from '../../Images/Drive.svg'
import mockup3 from '../../Images/Profile.svg'
import mockup4 from '../../Images/Garage.svg'
import {
    ArrowForward, ArrowRight,
    ColumnLeft,
    ColumnRight,
    HeroBtnWrapper,
    HeroContainer,
    HeroP, HeroTitle,
    HeroTitleRight,
    Mockup,
    Row, ServicesH1
} from "./WheelyAppElements";
import {Button} from "../../Utilities/ButtonElements";




const slideWidth = 30;



const _items = [
    {
        player: {
            title: ("Login").toUpperCase(),
            desc: 'The section where users log in to their account. If a user does not yet have a WHEELY account, he can also register in this section.',
            image: mockup
        },
    },
    {
        player: {
            title: ("Home").toUpperCase(),
            desc: "The home screen is a section where a user can see how many tokens he has, his vehicle, vehicle level, remaining energy and remaining distance. А user can also start driving from the home screen.",
            image: mockup1,
        },
    },
    {
        player: {
            title: 'Shane Van Boening',
            desc: 'The "South Dakota Kid" is hearing-impaired and uses a hearing aid, but it has not limited his ability.',
            image: mockup2,
        },
    },
    {
        player: {
            title: 'Mike Sigel',
            desc: 'Mike Sigel or "Captain Hook" as many like to call him is an American professional pool player with over 108 tournament wins.',
            image: mockup3,
        },
    },
    {
        player: {
            title: 'Willie Mosconi',
            desc: 'Nicknamed "Mr. Pocket Billiards," Willie Mosconi was among the first Billiard Congress of America Hall of Fame inductees.',
            image: mockup4,
        },
    },
];

const length = _items.length;
_items.push(..._items);

const sleep = (ms = 0) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
};

const createItem = (position, idx) => {
    const item = {
        styles: {
            transform: `translateX(${position * slideWidth}rem)`,
        },
        player: _items[idx].player,
    };

    switch (position) {
        case length - 1:
        case length + 1:
            item.styles = {...item.styles, filter: 'grayscale(2)'};
            break;
        case length:
            break;
        default:
            item.styles = {...item.styles, opacity: 0};
            break;
    }

    return item;
};

const CarouselSlideItem = ({pos, idx, activeIdx}) => {
    const item = createItem(pos, idx, activeIdx);
    // className={i === activeIdx ? 'dot active' : 'dot'}
    if (idx > 4)idx = idx-5;
    return (
        // <div className="block">
            <li className={idx === activeIdx ? "carousel__slide-item" : "carousel__slide-item active"} style={item.styles}>
                <div className="carousel__slide-item-img-link">
                    <img src={item.player.image} alt={item.player.title} />
                </div>
                <div className={idx === activeIdx ? "carousel-slide-item__body" : "carousel-slide-item__body active"}>
                    {/*<HeroTitleRight>{item.player.title}</HeroTitleRight>*/}
                    <h4>{item.player.title}</h4>
                    {/*<HeroP>you can earn game token.*/}
                    {/*</HeroP>*/}
                    {/*<p>{activeIdx}</p>*/}
                    <p>{item.player.desc}</p>
                </div>
            </li>
        // </div>

        // <li className={idx === activeIdx ? "carousel__slide-item" : "carousel__slide-item active"} style={item.styles}>
        //     <div className="carousel__slide-item-img-link">
        //         <img src={item.player.image} alt={item.player.title} />
        //     </div>
        //     <div className={idx === activeIdx ? "carousel-slide-item__body" : "carousel-slide-item__body active"}>
        //         {/*<HeroTitleRight>{item.player.title}</HeroTitleRight>*/}
        //         <h4>{item.player.title}</h4>
        //         {/*<HeroP>you can earn game token.*/}
        //         {/*</HeroP>*/}
        //         {/*<p>{activeIdx}</p>*/}
        //         <p>{item.player.desc}</p>
        //     </div>
        // </li>
    );
};

const keys = Array.from(Array(_items.length).keys());

const WheelyApp = () => {
    const [items, setItems] = React.useState(keys);
    const [isTicking, setIsTicking] = React.useState(false);
    const [activeIdx, setActiveIdx] = React.useState(0);
    const bigLength = items.length;

    const prevClick = (jump = 1) => {
        if (!isTicking) {
            setIsTicking(true);
            setItems((prev) => {
                return prev.map((_, i) => prev[(i + jump) % bigLength]);
            });
        }
    };

    const nextClick = (jump = 1) => {
        if (!isTicking) {
            setIsTicking(true);
            setItems((prev) => {
                return prev.map(
                    (_, i) => prev[(i - jump + bigLength) % bigLength],
                );
            });
        }
    };

    const handleDotClick = (idx) => {
        if (idx < activeIdx) prevClick(activeIdx - idx);
        if (idx > activeIdx) nextClick(idx - activeIdx);
    };

    React.useEffect(() => {
        if (isTicking) sleep(300).then(() => setIsTicking(false));
    }, [isTicking]);

    React.useEffect(() => {
        setActiveIdx((length - (items[0] % length)) % length) // prettier-ignore
    }, [items]);

    return (

        <HeroContainer id="wheely-app">


            <div className="carousel__wrap">

                <div className="carousel__inner">
                    <ServicesH1>
                        WHEELY APP
                    </ServicesH1>
                    <button className="carousel__btn carousel__btn--prev" onClick={() => prevClick()}>
                        <i className="carousel__btn-arrow carousel__btn-arrow--left" />
                    </button>
                    <div className="carousel__container">
                        <ul className="carousel__slide-list">
                            {items.map((pos, i) => (
                                <CarouselSlideItem
                                    key={i}
                                    idx={i}
                                    pos={pos}
                                    activeIdx={activeIdx}

                                />
                            ))}
                        </ul>
                    </div>
                    <button className="carousel__btn carousel__btn--next" onClick={() => nextClick()}>
                        <i className="carousel__btn-arrow carousel__btn-arrow--right" />
                    </button>
                    <div className="carousel__dots">
                        {items.slice(0, length).map((pos, i) => (
                            <button
                                key={i}
                                onClick={() => handleDotClick(i)}
                                className={i === activeIdx ? 'dot active' : 'dot'}
                            />
                        ))}
                    </div>
                </div>
            </div>

        </HeroContainer>


    );
};


export default WheelyApp


